        {
            int maxProfit = 0;
            int minPrice = INT_MAX;
            for (int i = 0; i < prices.size(); i++) {
                if (prices[i] < minPrice)
                    minPrice = min(minPrice , prices[i]);
                maxProfit = max(maxProfit, prices[i] - minPrice);
            }
            return maxProfit;
