class Solution {
public:
    int majorityElement(vector<int>& nums) {
        unordered_map<int, int> mapNums;
        for (int i = 0; i < nums.size(); ++i) {
            ++mapNums[nums[i]];
            if (mapNums[nums[i]] > nums.size() / 2) return nums[i];
        }
        return 0;
    }
};
